// --- CONFIGURATION ---
const PERIOD = 14;
const ATR_MAX_THRESHOLD = 0.03; 
const RSI_NEUTRAL_MIN = 48;
const RSI_NEUTRAL_MAX = 52;

// --- GET DATA FROM CANDLES ---
const allItems = $items('Historical Candles');
const candleData = allItems.map(item => item.json);

// --- GET ORIGINAL WEBHOOK DATA (Pass-Through) ---
// Fix: Grab data from Webhook to ensure trend/status aren't lost in Test Mode
let webhookData = {};
try {
    webhookData = $('Webhook').first().json;
} catch (e) {}

let current_price = 0;
if (Array.isArray(candleData) && candleData.length > 0) {
    // Index 0 is the most recent candle in Coinbase API
    const lastCandle = candleData[0]; 
    // Support both Finnhub {c:[]} and Coinbase [[t,l,h,o,c,v]] formats
    if (Array.isArray(lastCandle)) {
        current_price = parseFloat(lastCandle[4]); // Coinbase
    } else {
        current_price = parseFloat(lastCandle['c'] || 0); // Flat object fallback
    }
}

if (current_price === 0) {
    return [{ json: { signal_blocked: true, reason: "Error: No price in candles" } }];
}

// --- PARSE CANDLES FOR CALCULATION ---
// Handle Coinbase Format [[time, low, high, open, close, vol], ...]
const candles = candleData.map(c => ({
    close: Array.isArray(c) ? parseFloat(c[4]) : parseFloat(c['4'] || c.close || 0),
    high: Array.isArray(c) ? parseFloat(c[2]) : parseFloat(c['2'] || c.high || 0),
    low: Array.isArray(c) ? parseFloat(c[1]) : parseFloat(c['1'] || c.low || 0)
}));

// --- CALCULATION FUNCTIONS ---
function calculateATR(data, p) {
    let sum = 0;
    for (let i = 0; i < p; i++) {
        const c = data[i];
        const prev = data[i+1] ? data[i+1].close : c.close;
        sum += Math.max(c.high - c.low, Math.abs(c.high - prev), Math.abs(c.low - prev));
    }
    return sum / p;
}

function calculateRSI(data, p) {
    let g = 0, l = 0;
    for (let i = 0; i < p; i++) {
        const diff = data[i].close - data[i+1].close;
        if (diff > 0) g += diff; else l -= diff;
    }
    if (l === 0) return 100;
    return 100 - (100 / (1 + ((g/p) / (l/p))));
}

const atr_v = calculateATR(candles, PERIOD);
const rsi_v = calculateRSI(candles, PERIOD);
const atr_p = (atr_v / current_price);

let blocked = false;
let msg = "OK";

if (atr_p > ATR_MAX_THRESHOLD) {
    blocked = true;
    msg = "High Volatility";
}
if (rsi_v >= RSI_NEUTRAL_MIN && rsi_v <= RSI_NEUTRAL_MAX) {
    blocked = true;
    msg += " | RSI Neutral";
}

return [{ json: {
    ...webhookData, // <--- CRITICAL FIX: Pass through original fields (trend, etc.)
    current_price: current_price,
    atr_value: atr_v,
    atr_percentage: atr_p,
    rsi_value: rsi_v,
    signal_blocked: blocked,
    reason_code4: msg
}}];

// Universal Parser V2
const aiResponse = items[0].json;
const livePrice = parseFloat($items("Code/Function (ATR/RSI Filter)")[0].json.current_price || 0);

// Ensure confidence score lands in the correct variable
const signal = aiResponse.signal || "WAIT";
// Fix: Gemini can send 'confidence' OR 'confidence_score' - handle both
const conf = aiResponse.confidence || aiResponse.confidence_score || 0;

return {
    json: {
        ...aiResponse,
        final_signal: signal,
        current_price: livePrice,
        confidence_score: conf // Use 'conf' so Risk Filter always sees a number
    }
};
